import React, { useState } from "react";
import { CheckCircle } from "lucide-react";
import "./App.css";

function App() {
  const [page, setPage] = useState("Home");
  const [pets, setPets] = useState([]);

  const handleAddPet = (newPet) => {
    setPets([...pets, newPet]);
  };

  return (
    <div className="App" style={{ fontFamily: "Arial, sans-serif" }}>
      <header
        style={{
          background: "#007bff",
          color: "#fff",
          padding: "1rem",
          textAlign: "center",
        }}
      >
        <h1>Pets United</h1>
        <nav>
          <button onClick={() => setPage("Home")} className="nav-btn">
            Home
          </button>
          <button onClick={() => setPage("AvailablePets")} className="nav-btn">
            Available Pets
          </button>
          <button onClick={() => setPage("GetInvolved")} className="nav-btn">
            Get Involved
          </button>
        </nav>
      </header>

      <main style={{ padding: "2rem" }}>
        {page === "Home" && <HomePage />}
        {page === "AvailablePets" && <AvailablePets pets={pets} />}
        {page === "GetInvolved" && (
          <GetInvolvedPage onAddPet={handleAddPet} onNavigate={setPage} />
        )}
      </main>
    </div>
  );
}

function HomePage() {
  return (
    <div style={{ textAlign: "center" }}>
      <h2>Welcome to Pets United üêæ</h2>
      <p>Find your perfect companion or help one find a home.</p>
    </div>
  );
}

function AvailablePets({ pets }) {
  if (pets.length === 0) {
    return (
      <p style={{ textAlign: "center" }}>
        No pets listed yet. Add one through the Get Involved form!
      </p>
    );
  }

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
        gap: "1rem",
      }}
    >
      {pets.map((pet, index) => (
        <div
          key={index}
          style={{
            border: "1px solid #ddd",
            borderRadius: "12px",
            padding: "1rem",
            background: "#fff",
            boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
          }}
        >
          <img
            src={pet.imageUrl}
            alt={pet.name}
            style={{
              width: "100%",
              borderRadius: "8px",
              objectFit: "cover",
              maxHeight: "200px",
            }}
          />
          <h3>{pet.name}</h3>
          <p>
            <strong>Species:</strong> {pet.species}
          </p>
          <p>
            <strong>Breed:</strong> {pet.breed}
          </p>
          <p>
            <strong>Age:</strong> {pet.age}
          </p>
        </div>
      ))}
    </div>
  );
}

function GetInvolvedPage({ onAddPet, onNavigate }) {
  const [species, setSpecies] = useState("Dog");
  const [name, setName] = useState("");
  const [breed, setBreed] = useState("");
  const [age, setAge] = useState("");
  const [imageUrl, setImageUrl] = useState(null);
  const [trainingLevel, setTrainingLevel] = useState("Beginner");
  const [litterTrained, setLitterTrained] = useState(true);
  const [isSubmitted, setIsSubmitted] = useState(false);

  // handle image selection
  const handleImageChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setImageUrl(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !breed || !age || !imageUrl) {
      alert("Please fill out all fields and upload an image.");
      return;
    }
    const petAge = parseInt(age, 10);
    if (isNaN(petAge) || petAge < 0) {
      alert("Please enter a valid age.");
      return;
    }

    const newPet =
      species === "Dog"
        ? { name, species, breed, age: petAge, imageUrl, trainingLevel }
        : { name, species, breed, age: petAge, imageUrl, litterTrained };

    onAddPet(newPet);
    setIsSubmitted(true);
    setTimeout(() => onNavigate("AvailablePets"), 800);
  };

  if (isSubmitted) {
    return (
      <div style={{ textAlign: "center", paddingTop: "2rem" }}>
        <CheckCircle size={48} color="#10b981" />
        <h3>Thank You!</h3>
        <p>Your pet has been listed for adoption. Redirecting...</p>
      </div>
    );
  }

  return (
    <div
      style={{
        maxWidth: "500px",
        margin: "0 auto",
        background: "#fff",
        borderRadius: "12px",
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        padding: "1.5rem",
      }}
    >
      <h2>List a Pet for Adoption</h2>
      <form onSubmit={handleSubmit}>
        {/* IMAGE UPLOAD */}
        <div style={{ marginBottom: "1rem" }}>
          <label style={{ display: "block", marginBottom: "0.3rem" }}>
            Pet Photo
          </label>

          <div
            style={{
              border: "2px dashed #bbb",
              borderRadius: "12px",
              padding: "1rem",
              textAlign: "center",
              cursor: "pointer",
              background: "#fafafa",
              transition: "0.3s",
            }}
            onClick={() => document.getElementById("petPhoto").click()}
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => {
              e.preventDefault();
              if (e.dataTransfer.files?.[0]) {
                const file = e.dataTransfer.files[0];
                const reader = new FileReader();
                reader.onloadend = () => setImageUrl(reader.result);
                reader.readAsDataURL(file);
              }
            }}
          >
            {imageUrl ? (
              <img
                src={imageUrl}
                alt="Preview"
                style={{
                  width: "100%",
                  maxWidth: "300px",
                  borderRadius: "8px",
                  objectFit: "cover",
                  boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
                }}
              />
            ) : (
              <div style={{ color: "#888", fontSize: "0.95rem" }}>
                <p style={{ marginBottom: "0.3rem" }}>Click or Drag & Drop</p>
                <small>PNG, JPG, GIF ‚Äî up to 10MB</small>
              </div>
            )}
          </div>

          <input
            type="file"
            id="petPhoto"
            accept="image/*"
            onChange={handleImageChange}
            style={{ display: "none" }}
          />
        </div>

        {/* OTHER FIELDS */}
        <label>Species</label>
        <div style={{ marginBottom: "1rem" }}>
          <label style={{ marginRight: "1rem" }}>
            <input
              type="radio"
              value="Dog"
              checked={species === "Dog"}
              onChange={() => setSpecies("Dog")}
            />{" "}
            Dog
          </label>
          <label>
            <input
              type="radio"
              value="Cat"
              checked={species === "Cat"}
              onChange={() => setSpecies("Cat")}
            />{" "}
            Cat
          </label>
        </div>

        <label>Name</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={{
            display: "block",
            width: "100%",
            marginBottom: "1rem",
            padding: "0.5rem",
          }}
        />

        <label>Breed</label>
        <input
          type="text"
          value={breed}
          onChange={(e) => setBreed(e.target.value)}
          required
          style={{
            display: "block",
            width: "100%",
            marginBottom: "1rem",
            padding: "0.5rem",
          }}
        />

        <label>Age</label>
        <input
          type="number"
          value={age}
          onChange={(e) => setAge(e.target.value)}
          required
          style={{
            display: "block",
            width: "100%",
            marginBottom: "1rem",
            padding: "0.5rem",
          }}
        />

        {species === "Dog" ? (
          <>
            <label>Training Level</label>
            <select
              value={trainingLevel}
              onChange={(e) => setTrainingLevel(e.target.value)}
              style={{
                display: "block",
                width: "100%",
                marginBottom: "1rem",
                padding: "0.5rem",
              }}
            >
              <option>Beginner</option>
              <option>Intermediate</option>
              <option>Advanced</option>
            </select>
          </>
        ) : (
          <label style={{ display: "block", marginBottom: "1rem" }}>
            <input
              type="checkbox"
              checked={litterTrained}
              onChange={(e) => setLitterTrained(e.target.checked)}
            />{" "}
            Litter Trained
          </label>
        )}

        <button
          type="submit"
          style={{
            width: "100%",
            background: "#007bff",
            color: "white",
            border: "none",
            padding: "0.75rem",
            borderRadius: "8px",
            cursor: "pointer",
            fontSize: "1rem",
          }}
        >
          Submit Pet for Adoption
        </button>
      </form>
    </div>
  );
}

export default App;
