  );
}

function AdoptionFormPage({ pet, onProcessAdoption, onBack }) {
  const [adopterName, setAdopterName] = useState("");
  const [adopterContact, setAdopterContact] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);

  if (!pet) {
    return (
      <div className="container">
        <div style={{ textAlign: "center", padding: 30 }}>
          <p style={{ color: "#ef4444" }}>Error: No pet selected.</p>
          <button onClick={onBack} className="nav-button" style={{ color: "#4f46e5" }}>Go back</button>
        </div>
      </div>
    );
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!adopterName.trim() || !adopterContact.trim()) {
      alert("Please fill out all fields.");
      return;
    }
    onProcessAdoption(pet.id, { name: adopterName, contact: adopterContact });
    setIsSubmitted(true);
  };

  if (isSubmitted) {
    return (
      <div className="container" style={{ paddingTop: 40 }}>
        <div className="card" style={{ textAlign: "center" }}>
          <CheckCircle size={48} color="#10b981" />
          <h3>Success!</h3>
          <p>Adoption of <strong>{pet.name}</strong> has been registered in PetsUnited!</p>
          <button onClick={onBack} className="nav-button" style={{ marginTop: 12, background: "#4f46e5", color: "#fff" }}>
            Back to Available Pets
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container" style={{ paddingTop: 20 }}>
      <button onClick={onBack} className="nav-button" style={{ marginBottom: 12 }}>
        <ArrowLeft size={16} /> Back to Pets
      </button>

      <div className="card" style={{ padding: 20 }}>
        <h3>Adoption Inquiry Form</h3>
        <p>You&apos;re one step closer to adopting <strong style={{ color: "#4f46e5" }}>{pet.name}</strong>!</p>

        <form onSubmit={handleSubmit} className="form" style={{ marginTop: 12 }}>
          <div>
            <label>Name</label>
            <input className="input" value={adopterName} onChange={(e) => setAdopterName(e.target.value)} required />
          </div>
          <div>
            <label>Contact Email or Phone</label>
            <input className="input" value={adopterContact} onChange={(e) => setAdopterContact(e.target.value)} required />
          </div>
          <button type="submit" className="nav-button" style={{ background: "#4f46e5", color: "#fff", marginTop: 8 }}>
            Register Inquiry
          </button>
        </form>
      </div>
    </div>
  );
}

function GetInvolvedPage({ onAddPet, onNavigate }) {
  const [species, setSpecies] = useState("Dog");
  const [name, setName] = useState("");
  const [breed, setBreed] = useState("");
  const [age, setAge] = useState("");
  const [imageUrl, setImageUrl] = useState(null);
  const [trainingLevel, setTrainingLevel] = useState("Beginner");
  const [litterTrained, setLitterTrained] = useState(true);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setImageUrl(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name.trim() || !breed.trim() || !age || !imageUrl) {
      alert("Please fill out all fields and upload an image.");
      return;
    }
    const petAge = parseInt(age, 10);
    if (isNaN(petAge) || petAge < 0) {
      alert("Please enter a valid age.");
      return;
    }
    let newPet;
    if (species === "Dog") {
      newPet = { name, species, breed, age: petAge, imageUrl, trainingLevel };
    } else {
      newPet = { name, species, breed, age: petAge, imageUrl, litterTrained };
    }
    onAddPet(newPet);
    setIsSubmitted(true);
    setTimeout(() => onNavigate("AvailablePets"), 700);
  };

  if (isSubmitted) {
    return (
      <div className="container" style={{ paddingTop: 30 }}>
        <div className="card" style={{ textAlign: "center" }}>
          <CheckCircle size={48} color="#10b981" />
          <h3>Thank You!</h3>
          <p>Your pet has been listed for adoption. Redirecting to available pets...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card" style={{ padding: 16 }}>
        <h3>List a Pet for Adoption</h3>
        <p>Help us find a new home for a companion in need. Please provide details below.</p>

        <form onSubmit={handleSubmit} className="form" style={{ marginTop: 12 }}>
          <div>
            <label>Pet Photo</label>
            <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
              <div style={{ width: 120, height: 80, background: "#f8fafc", borderRadius: 8, display: "flex", alignItems: "center", justifyContent: "center" }}>
                {imageUrl ? <img src={imageUrl} alt="preview" style={{ width: "100%", height: "100%", objectFit: "cover", borderRadius: 8 }} /> : <UploadCloud />}
              </div>
              <div>
                <input type="file" accept="image/*" onChange={handleImageChange} />
                <div style={{ fontSize: 12, color: "#64748b", marginTop: 6 }}>PNG, JPG, GIF up to 10MB</div>
              </div>
            </div>
          </div>

          <div>
            <label>Species</label>
            <div className="row" style={{ marginTop: 6 }}>
              <label><input type="radio" value="Dog" checked={species === "Dog"} onChange={() => setSpecies("Dog")} /> Dog</label>
              <label><input type="radio" value="Cat" checked={species === "Cat"} onChange={() => setSpecies("Cat")} /> Cat</label>
            </div>
          </div>

          <div>
            <label>Name</label>
            <input className="input" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>

          <div className="row">
            <div style={{ flex: 1 }}>
              <label>Breed</label>
              <input className="input" value={breed} onChange={(e) => setBreed(e.target.value)} required />
            </div>
            <div>
              <label>Age</label>
              <input className="input small" value={age} onChange={(e) => setAge(e.target.value)} type="number" min="0" required />
            </div>
          </div>

          {species === "Dog" && (
            <div>
              <label>Training Level</label>
              <select className="input" value={trainingLevel} onChange={(e) => setTrainingLevel(e.target.value)}>
                <option>Beginner</option>
                <option>Intermediate</option>
                <option>Advanced</option>
                <option>Expert</option>
              </select>
            </div>
          )}

          {species === "Cat" && (
            <div>
              <label>Litter Trained?</label>
              <div className="row" style={{ marginTop: 6 }}>
                <label><input type="radio" checked={litterTrained === true} onChange={() => setLitterTrained(true)} /> Yes</label>
                <label><input type="radio" checked={litterTrained === false} onChange={() => setLitterTrained(false)} /> No</label>
              </div>
            </div>
          )}

          <button type="submit" className="nav-button" style={{ background: "#4f46e5", color: "#fff", marginTop: 6 }}>
            Submit Pet for Adoption
          </button>
        </form>
      </div>
    </div>
  );
}

