function GetInvolvedPage({ onAddPet, onNavigate }) {
  const [species, setSpecies] = useState("Dog");
  const [name, setName] = useState("");
  const [breed, setBreed] = useState("");
  const [age, setAge] = useState("");
  const [imageUrl, setImageUrl] = useState(null);
  const [trainingLevel, setTrainingLevel] = useState("Beginner");
  const [litterTrained, setLitterTrained] = useState(true);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setImageUrl(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name.trim() || !breed.trim() || !age || !imageUrl) {
      alert("Please fill out all fields and upload an image.");
      return;
    }

    const petAge = parseInt(age, 10);
    if (isNaN(petAge) || petAge < 0) {
      alert("Please enter a valid age.");
      return;
    }

    const newPet =
      species === "Dog"
        ? { name, species, breed, age: petAge, imageUrl, trainingLevel }
        : { name, species, breed, age: petAge, imageUrl, litterTrained };

    onAddPet(newPet);
    setIsSubmitted(true);
    setTimeout(() => onNavigate("AvailablePets"), 800);
  };

  if (isSubmitted) {
    return (
      <div className="container" style={{ paddingTop: 30 }}>
        <div className="card" style={{ textAlign: "center" }}>
          <CheckCircle size={48} color="#10b981" />
          <h3>Thank You!</h3>
          <p>Your pet has been listed for adoption. Redirecting to available pets...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card" style={{ padding: 16 }}>
        <h3>List a Pet for Adoption</h3>
        <p>Help us find a new home for a companion in need. Please provide details below.</p>

        <form onSubmit={handleSubmit} className="form" style={{ marginTop: 12 }}>
          {/* IMAGE UPLOAD BOX */}
          <div className="form-field" style={{ marginBottom: "1.2rem" }}>
            <label>Pet Photo</label>
            <div
              style={{
                position: "relative",
                border: "2px dashed #cbd5e1",
                borderRadius: "12px",
                width: "100%",
                height: "200px",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                background: "#f8fafc",
                overflow: "hidden",
                cursor: "pointer",
                transition: "0.3s",
              }}
              onClick={() => document.getElementById("petPhoto").click()}
              onDragOver={(e) => e.preventDefault()}
              onDrop={(e) => {
                e.preventDefault();
                const file = e.dataTransfer.files?.[0];
                if (file) {
                  const reader = new FileReader();
                  reader.onloadend = () => setImageUrl(reader.result);
                  reader.readAsDataURL(file);
                }
              }}
            >
              {imageUrl ? (
                <img
                  src={imageUrl}
                  alt="Preview"
                  style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    width: "100%",
                    height: "100%",
                    objectFit: "cover",
                    borderRadius: "12px",
                  }}
                />
              ) : (
                <div
                  style={{
                    textAlign: "center",
                    color: "#64748b",
                    fontSize: "0.95rem",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                  }}
                >
                  <UploadCloud size={36} />
                  <p style={{ marginTop: "6px" }}>Click or drag & drop to upload an image</p>
                  <small style={{ color: "#94a3b8" }}>PNG, JPG, GIF up to 10MB</small>
                </div>
              )}
            </div>

            <input
              type="file"
              id="petPhoto"
              accept="image/*"
              onChange={handleImageChange}
              style={{ display: "none" }}
            />
          </div>

          {/* OTHER FIELDS */}
          <div className="form-field">
            <label>Species</label>
            <div className="radio-group" style={{ marginBottom: "1rem" }}>
              <label style={{ marginRight: "1rem" }}>
                <input
                  type="radio"
                  name="species"
                  value="Dog"
                  checked={species === "Dog"}
                  onChange={() => setSpecies("Dog")}
                />{" "}
                Dog
              </label>
              <label>
                <input
                  type="radio"
                  name="species"
                  value="Cat"
                  checked={species === "Cat"}
                  onChange={() => setSpecies("Cat")}
                />{" "}
                Cat
              </label>
            </div>
          </div>

          <div className="form-field">
            <label>Name</label>
            <input
              type="text"
              className="input"
              placeholder="Enter name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #d1d5db",
                borderRadius: "6px",
              }}
            />
          </div>

          <div className="form-field">
            <label>Breed</label>
            <input
              type="text"
              className="input"
              placeholder="Enter breed"
              value={breed}
              onChange={(e) => setBreed(e.target.value)}
              required
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #d1d5db",
                borderRadius: "6px",
              }}
            />
          </div>

          <div className="form-field">
            <label>Age</label>
            <input
              type="number"
              className="input"
              placeholder="Enter age"
              value={age}
              onChange={(e) => setAge(e.target.value)}
              required
              style={{
                width: "100%",
                padding: "0.5rem",
                border: "1px solid #d1d5db",
                borderRadius: "6px",
              }}
            />
          </div>

          {species === "Dog" ? (
            <div className="form-field">
              <label>Training Level</label>
              <select
                className="input"
                value={trainingLevel}
                onChange={(e) => setTrainingLevel(e.target.value)}
                style={{
                  width: "100%",
                  padding: "0.5rem",
