                <option>Advanced</option>
              </select>
            </div>
          ) : (
            <div className="form-field">
              <label>
                <input
                  type="checkbox"
                  checked={litterTrained}
                  onChange={(e) => setLitterTrained(e.target.checked)}
                />{" "}
                Litter Trained
              </label>
            </div>
          )}

          {/* FIXED BUTTON SIZE */}
          <button type="submit" className="form-btn">
            Submit Pet for Adoption
          </button>
        </form>
      </div>
    </div>
  );
}

